import {useFormik} from "formik";
import React from "react";

const initialValues={
       email:'',
       password:'',
}
const validationSchema=values=>{
    const errors={};
    if(!values.email){
      errors.email="Required";
    }else if(!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)){
        errors.email="invalid email address";
    }

    if(!values.password){
       errors.password="Required";
    }

    return errors;
}
const onSubmit=values=>{
    console.log("values",values);
}
function Logins(){

    const formik=useFormik({
        initialValues,
        validationSchema,
        onSubmit
    })

    return (
        <form onSubmit={formik.handleSubmit}>
        <div>
        <label htmlFor="email" placeholder="PLease enter your email">Email</label>
        <input 
        type="text" 
        name="email" 
        id="email"
        onChange={formik.handleChange}
        value={formik.values.eamil}
        onBlur={formik.handleBlur}
        />
        {formik.touched.eamil && formik.errors.email ? (<div>{formik.errors.email}</div>):null}
        </div>

        <div>
        <label htmlFor="password" placeholder="enter your password">Password</label>
        <input 
        type="text" 
        name="password" 
        id="password"
        onChange={formik.handleChange}
        value={formik.values.password}
        onBlur={formik.handleBlur}
        />
        {formik.touched.password && formik.errors.password ? (<div>{formik.errors.password}</div>):null}
        </div>

        <button type="submit"></button>
        </form>
    )
}
export default Logins;
